            transDisplayed = true;
        }
        else
        {
            parent.Children.Remove(rtb);
            (sender as Button).Content = "Display Transcript";
            transDisplayed = false;
        }
    }
    private void DisplayTranscript()
    {
        Panel parent = (player.Parent as Panel);
        parent.Children.Add(rtb);
    }

