private void TextBox_KeyDown(object sender, 
    System.Windows.Input.KeyEventArgs e)
{
    if (e.Key == System.Windows.Input.Key.Enter) (sender as TextBox)
        .GetBindingExpression(TextBox.TextProperty).UpdateSource();
